mapper.select:
select order_id , order_status , goods_total , payment_money from t_sys_orderfather where is_deleted = 0 and user_code = #{userCode}
and order_status = #{orderStatus}
and order_status = #{orderStatus}
and order_status in (3,4)

update.foreach:
update t_sys_goods
set stock = stock - #{mapList.goodsCnt2}
where sku_no = #{mapList.skuNo}
