mapper.select:
select a.order_id orderId, a.payment_money paymentMoney, a.order_status orderStatus, a.store_no storeNo, c.user_name userName, c.phone userPhone, a.create_time createTime from t_sys_orderfather a, t_client_info b, t_sys_user c where a.user_code = b.client_user_code and a.user_code = c.user_code and a.is_deleted = 0
and c.user_name like concat('%', #{userName}, '%')
and a.order_id like concat('%', #{orderId}, '%')
and c.phone like concat('%', #{userPhone}, '%')
and a.order_status =#{orderStatus}
AND a.pay_time >= #{startTime}
AND a.pay_time <= #{endTime}
AND a.store_no = #{storeNo2}

select role
from t_sys_user
where user_code = #{userCode}

select a.store_no
from t_sys_store a,
t_sys_user b
where
a.user_acct = b.user_acct
and b.user_code = #{userCode}
